function pageLoad(){showQuote();for(var e=document.getElementsByClassName("item"),t=0;t<e.length;t++)e[t].getElementsByTagName("button")[0].onclick=function(){addToTotal(parseFloat(document.getElementById(this.parentNode.id).getElementsByClassName("price-of-toy")[0].innerText)),addToCart(this.parentNode.id)}}var possibleQuotes=["What a great day to buy toys!","We sell toys - it's what we do!","If you came here to by cookies, you came to the wrong place...","Oh happy day!","Mary says Legos are the best!","Toys, Toys and more Toys!","We have free shipping for orders over $75!","5-day shipping guaranteed!","Now selling birthday party themed decorations!","Spend $100 and get $5 towards your next purchase!"],thankYouQuotes=["Thank you for shopping at Mary's Toys!","Thanks!","Come again!","Thanks for your purchase! We hope you shop again!","Is that all you have bought? Don't be shy!"],counter=0;function addToTotal(e){e=Number(e),counter+=e,document.getElementById("cart-total").innerHTML="",e=counter.toFixed(2);document.getElementById("cart-total").innerHTML="$"+e}function addToCart(e){e=document.getElementById(e),e.parentNode.removeChild(e),e=Number(document.getElementById("items-in-cart").innerHTML);document.getElementById("items-in-cart").innerHTML=e+1,showThankYouQuote()}function showQuote(){var e=possibleQuotes[Math.floor(10*Math.random())];document.getElementById("quoteContainer").innerHTML=e}function showThankYouQuote(){var e=thankYouQuotes[Math.floor(5*Math.random())];document.getElementById("quoteContainer").innerHTML="<strong>"+e+"</strong>"}